mapper.insert:
	{call Board_Ins(#{boardtitle, mode=IN, jdbcType=VARCHAR, javaType=java.lang.String}
					,#{color, mode=IN, jdbcType=VARCHAR, javaType=java.lang.String}
	)}

mapper.select:
{call Board_Ins(#{mem_id, mode=IN, jdbcType=VARHCHAR, javaType=java.lang.String}
               ,#{r_cursor, mode=OUT, jdbcType=CURSOR, javaType=java.lang.String}
)}

mapper.update:
update board
set board_update = 0
where board_no = #{id}
